package uni.ldts.physics;public class Body {    private final AABB aabb;    /**     * Create a simple physics body     * @param position body position     * @param size size of body     */    public Body(Vector2D position, Vector2D size) {        this.aabb = new AABB(position,size);    }    /**     * Create a physics body     * @param x initial x     * @param y initial y     * @param sizeX size x-axis     * @param sizeY size y-axis     */    public Body(int x, int y, int sizeX, int sizeY) {        this.aabb = new AABB(x,y,sizeX,sizeY);    }    /**     * Copy constructor     * @param b body to copy     */    public Body(Body b) {        this.aabb = b.aabb;    }    public Vector2D getPos() { return this.aabb.getMin(); }    public Vector2D size() { return this.aabb.getSize(); }    public AABB getAABB() { return this.aabb; }}